using System; using System.Net.Http; using System.Net.Http.Json; using System.Threading.Tasks; namespace TestApi { public class WeatherContext { public int Temperature { get; set; }  public string Weather_Condition { get; set; } = string.Empty; public string Tide_Condition { get; set; } = string.Empty; } public class Recommendation { public string Fish_Target { get; set; } = string.Empty; public string Gear { get; set; } = string.Empty; public string Axis_Line { get; set; } = string.Empty; public string Leader { get; set; } = string.Empty; public string Hook { get; set; } = string.Empty; public string Bait { get; set; } = string.Empty; } public class ComboRecommendationResponse { public WeatherContext Context { get; set; } = new(); public Recommendation Recommendation { get; set; } = new(); public string Message { get; set; } = string.Empty; } class Program { static async Task Main() { var client = new HttpClient(); try { var res = await client.GetFromJsonAsync<ComboRecommendationResponse>("https://satnguapp-backend-production.up.railway.app/recommendation/?lat=10.2972&lon=105.7502"); Console.WriteLine("Success: " + res.Recommendation.Fish_Target); } catch (Exception e) { Console.WriteLine("ERROR: " + e.Message); } } } }
